import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const CourseDetails = () => {
  const { courseName } = useParams();
  const [course, setCourse] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchCourse = async () => {
      try {
        const response = await fetch(`http://192.168.47.66:8080/course/get/${courseName}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Course not found');
        }

        const data = await response.json();
        setCourse(data);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchCourse();
  }, [courseName, navigate]);

  if (error) return <p className="text-red-500">{error}</p>;
  if (!course) return <p>Loading...</p>;

  return (
    <div className="p-10 max-w-3xl mx-auto bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-2">{course.courseName}</h2>
      <p><strong>Fee:</strong> â‚¹{course.courseFee}</p>
      <p><strong>Duration:</strong> {course.courseDuration}</p>
      <p><strong>Syllabus:</strong> {course.courseSilybus}</p>
      <p className="mb-4"><strong>Description:</strong> {course.description}</p>

      <a
        href={course.pdfContentUrl}
        className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 mr-4"
        download
      >
        Download PDF
      </a>

      <button
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        onClick={() => alert('Payment Integration Coming Soon')}
      >
        Buy Course
      </button>
    </div>
  );
};

export default CourseDetails;
