  const [imageError, setImageError] = useState(false);
  const defaultImageUrl = "https://via.placeholder.com/400x200?text=Image+Not+Available"; // Or your own local placeholder

  const handleImageError = ( ) => {
    setImageError(true);
  };

  return (
    <div className="border rounded-lg overflow-hidden shadow hover:shadow-lg transition">
      <img
        src={imageError ? defaultImageUrl : `https://via.placeholder.com/400x200?text=${encodeURIComponent(courseName )}`}
