import React from 'react';
import { Link } from "react-router-dom";

const CourseCard = ({
  id,
  courseName,
  courseFee,
  courseDuration,
  courseSilybus,
  description,
  pdfContentUrl
}) => {
  return (
    <div className="border rounded-lg overflow-hidden shadow-md bg-white">
      <img
        className="w-full h-40 object-cover"
        src="https://cdn.elearningindustry.com/wp-content/uploads/2022/09/shutterstock_2118853421.jpg" // fallback or static image
        alt={courseName}
      />
      <div className="p-4">
        <h3 className="text-xl font-bold mb-1 text-blue-700">{courseName}</h3>
        <p className="text-sm text-gray-600">üßæ Description: {description}</p>
        <p className="text-sm text-gray-600">üïí Duration: {courseDuration}</p>
        <p className="text-sm text-gray-600">üìò Syllabus: {courseSilybus}</p>
        <div className="flex justify-between items-center mt-4">
          <span className="text-green-600 font-bold text-lg">‚Çπ{courseFee}</span>
          <a
            href={pdfContentUrl}
            className="text-blue-600 hover:underline"
            target="_blank"
            rel="noopener noreferrer"
            download
          >
            ‚¨áÔ∏è View PDF
          </a>
        </div>
      </div>
    </div>
  );
};

export default CourseCard;
