import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const CourseDetails = () => {
  const { courseName } = useParams();
  const [course, setCourse] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchCourse = async () => {
      try {
        const response = await fetch(`http://192.168.47.66:8080/course/get/${courseName}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Failed to load course details');
        }

        const data = await response.json();
        setCourse(data);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchCourse();
  }, [courseName, navigate]);

  if (error) return <p className="text-red-500 text-center mt-10">{error}</p>;
  if (!course) return <p className="text-center mt-10">Loading...</p>;

  return (
    <div className="max-w-4xl mx-auto px-6 py-12 bg-white shadow rounded-lg">
      <h2 className="text-3xl font-bold text-blue-800 mb-4">{course.courseName}</h2>
      <p className="text-gray-700 mb-2"><strong>Fee:</strong> â‚¹{course.courseFee}</p>
      <p className="text-gray-700 mb-2"><strong>Duration:</strong> {course.courseDuration}</p>
      <p className="text-gray-700 mb-2"><strong>Syllabus:</strong> {course.courseSilybus}</p>
      <p className="text-gray-700 mb-6"><strong>Description:</strong> {course.description}</p>

      <div className="flex gap-4">
        <a
          href={course.pdfContentUrl}
          target="_blank"
          rel="noopener noreferrer"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          ðŸ“„ View/Download Syllabus
        </a>

        <button
          onClick={() => alert('Buy functionality coming soon')}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          ðŸ›’ Buy Course
        </button>
      </div>
    </div>
  );
};

export default CourseDetails;
